mapper.select:
select
id, user_id, project_name, project_content, created_by, creation_date, last_updated_by, last_update_date
from project
is_delete=0

        and id = #{id}


        and user_id = #{userId}


        and project_name LIKE concat('%', #{projectName},'%')


        and project_content = #{projectContent}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_updated_by = #{lastUpdatedBy}


        and last_update_date = #{lastUpdateDate}




        order by ${sidx} ${order}


        order by id desc



    limit #{offset}, #{limit}

select
id
from project


        and id = #{id}


        and user_id = #{userId}


        and project_name = #{projectName}


        and project_content = #{projectContent}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_updated_by = #{lastUpdatedBy}


        and last_update_date = #{lastUpdateDate}


limit 1

select count(1)
from project


        and id = #{id}


        and user_id = #{userId}


        and project_name LIKE concat('%', #{projectName},'%')


        and project_content = #{projectContent}


        and created_by = #{createdBy}


        and creation_date = #{creationDate}


        and last_updated_by = #{lastUpdatedBy}


        and last_update_date = #{lastUpdateDate}

select
count(id)
from project
is_delete=0

        and id = #{id}


        and user_id = #{userId}


        and project_name LIKE concat('%', #{projectName},'%')


        and project_content = #{projectContent}


        and created_by = #{createdBy}


